template.script:
document.addEventListener('DOMContentLoaded', function() {
       var slider = document.querySelector('.center-slide');
       if (!slider) return;

       var slides = slider.querySelectorAll('.item');
       var currentSlide = 0;
       var slideCount = slides.length;

       function getSlidesToShow() {
           var width = window.innerWidth;
           if (width <= 767) return 1;
           if (width <= 991) return 2;
           return 3;
       }

       function showSlides() {
           var slidesToShow = getSlidesToShow();
           slides.forEach(function(slide, index) {
               if (index >= currentSlide && index < currentSlide + slidesToShow) {
                   slide.style.display = 'block';
               } else {
                   slide.style.display = 'none';
               }
               slide.style.marginRight = '10px';
               slide.style.marginLeft = '10px';
           });
           updateDots();
       }

       function createDots() {
           var dotContainer = document.createElement('div');
           dotContainer.className = 'slider-dots';
           var dotCount = Math.ceil(slideCount / getSlidesToShow());
           for (var i = 0; i < dotCount; i++) {
               var dot = document.createElement('span');
               dot.className = 'dot';
               dot.addEventListener('click', function() {
                   currentSlide = i * getSlidesToShow();
                   showSlides();
               });
               dotContainer.appendChild(dot);
           }
           slider.appendChild(dotContainer);
       }

       function updateDots() {
           var dots = slider.querySelectorAll('.dot');
           var activeDotIndex = Math.floor(currentSlide / getSlidesToShow());
           dots.forEach(function(dot, index) {
               dot.classList.toggle('active', index === activeDotIndex);
           });
       }

       createDots();
       showSlides();

       window.addEventListener('resize', showSlides);
   });
