template.script:
document.addEventListener('DOMContentLoaded', function() {
    var accordionItems = document.querySelectorAll('.sidebar_right_outer .res-submenu, .sidebar_right_outer .res-submenu, .accordion-item .faq-heading');

    accordionItems.forEach(function(item) {
        item.addEventListener('click', function() {
            var parent = this.closest('.accordion-item');
            var content = parent.querySelector('.accordian-text');
            var allItems = document.querySelectorAll('.accordion-item');

            allItems.forEach(function(otherItem) {
                if (otherItem !== parent) {
                    otherItem.classList.remove('active');
                    var otherContent = otherItem.querySelector('.accordian-text');
                    if (otherContent) {
                        otherContent.style.display = 'none';
                    }
                }
            });

            parent.classList.toggle('active');
            if (content) {
                content.style.display = content.style.display === 'none' ? 'block' : 'none';
            }
        });
    });
});
